// hide #back-top first
$("#back-top").hide();

// fade in #back-top

$(window).scroll(function () {
	if ($(this).scrollTop() > 100) {
		$('#back-top').fadeIn();
	} else {
		$('#back-top').fadeOut();
	}
});

// scroll body to 0px on click
$('#back-top a').on("click", function(){
	$('body,html').animate({
		scrollTop: 0
	}, 800);
	return false;
});

// Closes the Responsive Menu on Menu Item Click
$('.navbar-collapse ul li a').click(function() {
	$('.navbar-toggle:visible').click();
});




$('.bxslider').bxSlider({
      adaptiveHeight: false,   // keep viewport fixed
      auto: true,
      pause: 4500,
      speed: 600,
      controls: false,
      pager: true
});

 
  $(function(){
    $('.bio-toggle').on('click', function(e){
      e.preventDefault();
      var $btn = $(this);
      var target = $btn.data('target');
      var $bio = $(target);

      var expanded = $bio.hasClass('clamped') ? false : true; // current state before toggle
      $bio.toggleClass('clamped');                            // toggle clamp

      // Update button text + a11y state
      var nowExpanded = !$bio.hasClass('clamped');
      $btn.text(nowExpanded ? 'Read less' : 'Read more')
          .attr('aria-expanded', nowExpanded);
    });
  });
 
